import './input.css';
import React from 'react';
import {FormControl, FormGroup, FormLabel, HelpBlock, Checkbox, Radio, Button} from 'react-bootstrap';

class Input extends React.Component {
    constructor(props) {
      super(props);
      this.state = {country: '', currency: '', locale: '', origin: '', destination: '', outbound: '', inbound: ''};
  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      let name = event.target.name;
      let value = event.target.value;
      this.setState({[name]: value});
    }
  
    handleSubmit(event) {
      console.log(this.state)
      this.props.submitState();
      this.props.returnResults(this.state)
      event.preventDefault();

    }
  
    render() {
      return (
        <div>
            <header id="headersub">Find a Flight!</header>
            <form id="theform" onSubmit={this.handleSubmit}>
            <FormGroup controlId="formControlsText">
              <FormLabel>Country</FormLabel>
              <FormControl type="text" placeholder="Enter Country (ex. US)"  name='country' value={this.state.country} onChange={this.handleChange}/>
            </FormGroup> 
            <FormGroup controlId="formControlsText">
              <FormLabel>Currency</FormLabel>
              <FormControl type="text" placeholder="Enter Currency (ex. USD)"  name='currency' value={this.state.currency} onChange={this.handleChange}/>
            </FormGroup> 
            <FormGroup controlId="formControlsText">
              <FormLabel>Locale</FormLabel>
              <FormControl type="text" placeholder="Enter Locale (ex. en-US)"  name='locale' value={this.state.locale} onChange={this.handleChange}/>
            </FormGroup> 
            <FormGroup controlId="formControlsText">
              <FormLabel>Origin</FormLabel>
              <FormControl type="text" placeholder="Enter Origin (ex. JFK-sky)"  name='origin' value={this.state.origin} onChange={this.handleChange}/>
            </FormGroup> 
            <FormGroup controlId="formControlsText">
              <FormLabel>Destination</FormLabel>
              <FormControl type="text" placeholder="Enter Origin (ex. JFK-sky)"  name='destination' value={this.state.destination} onChange={this.handleChange}/>
            </FormGroup> 
            <FormGroup controlId="formControlsText">
              <FormLabel>Outbound Date</FormLabel>
              <FormControl type="text" placeholder="Enter Date (yyyy-dd-mm)"  name='outbound' value={this.state.outbound} onChange={this.handleChange}/>
            </FormGroup> 
            <FormGroup controlId="formControlsText">
              <FormLabel>Inbound Date</FormLabel>
              <FormControl type="text" placeholder="Enter Date (yyyy-dd-mm)" name='inbound' value={this.state.inbound} onChange={this.handleChange}/>
            </FormGroup> 
            <Button variant="primary" type="submit" value="Options">
              Submit
            </Button>
            </form>
        </div>
      );
    }
  }


export default Input;
