import logo from './logo.svg';
import './input.css';
import React from 'react';
import App from './App';

class Input extends React.Component {
    constructor(props) {
      super(props);
      this.state = {country: '', currency: '', locale: ''};
  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      this.setState({value: event.target.value});
    }
  
    handleSubmit(event) {
      < App/>
      alert('A name was submitted: ' + this.state.value);
      event.preventDefault();

    }
  
    render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <label>
            Country:
            <input type="text" value={this.state.country} onChange={this.handleChange} />
          </label>
          <label>
            Currency:
            <input type="text" value={this.state.currency} onChange={this.handleChange} />
          </label>
          <label>
            locale:
            <input type="text" value={this.state.locale} onChange={this.handleChange} />
          </label>
          <label>
            Name:
            <input type="text" value={this.state.value} onChange={this.handleChange} />
          </label>
          <input type="submit" value="Options" />
        </form>
      );
    }
  }


export default Input;
