import logo from './logo.svg';
import './input.css';
import React from 'react';
import App from './App';

class Input extends React.Component {
    constructor(props) {
      super(props);
      this.state = {country: '', currency: '', locale: '', origin: '', destination: '', outbound: '', inbound: '', submitted: false};
  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      let name = event.target.name;
      let value = event.target.value;
      this.setState({[name]: value});
    }
  
    handleSubmit(event) {
      alert('A name was submitted: ' + this.state.country + this.state.currency);
      this.setState({"submitted": true})
      event.preventDefault();

    }
  
    render() {
      return (
        <div>
            <form onSubmit={this.handleSubmit}>
            <label>
                Country:
                <input type="text" name='country' value={this.state.country} onChange={this.handleChange} />
            </label>
            <label>
                Currency:
                <input type="text" name='currency' value={this.state.currency} onChange={this.handleChange} />
            </label>
            <label>
                Locale:
                <input type="text" name='locale' value={this.state.locale} onChange={this.handleChange} />
            </label>
            <label>
                Origin:
                <input type="text" name='origin' value={this.state.origin} onChange={this.handleChange} />
            </label>
            <label>
                Destination:
                <input type="text" name='destination' value={this.state.destination} onChange={this.handleChange} />
            </label>
            <label>
                Outbound Date:
                <input type="text" name='outbound' value={this.state.outbound} onChange={this.handleChange} />
            </label>
            <label>
                Inbound Date:
                <input type="text" name='inbound' value={this.state.inbound} onChange={this.handleChange} />
            </label>
            <input type="submit" value="Options" />
            </form>

            {this.state.submitted ? <div>Hello</div> : <div>bye</div>}
        </div>
      );
    }
  }


export default Input;
