import './List.css';
import React from 'react';

class List extends React.Component {

    
    constructor(props) {
      super(props);
      this.count = 0
      this.state = {loading: false, data: null, number: 0, dropdown: "default", displayList: null, counter: 0};
      this.componentDidUpdate = this.componentDidUpdate.bind(this);
      this.run = this.run.bind(this);
      this.componentDidMount = this.componentDidMount.bind(this);
      this.onClickHandler = this.onClickHandler.bind(this);
      this.handleSelect = this.handleSelect.bind(this);
    }

    run(){
        var result = this.props.value;
        console.log("test")
        if(result != null){
          const request = require('request');
          require('JSON');

          const options = {
            method: 'GET',
            url: "https://skyscanner-skyscanner-flight-search-v1.p.rapidapi.com/apiservices/browseroutes/v1.0/" + result["country"] + "/" + result["currency"] + "/" + result["locale"] +
            "/" + result["origin"] + "/" + result["destination"] + "/" + result["outbound"],
            qs: {inboundpartialdate: result["inbound"]},
            json: true,
            headers: {
              'x-rapidapi-key': 'eee60225dfmsh483cdc041112e38p1614f2jsn1e3e3516465d',
              'x-rapidapi-host': 'skyscanner-skyscanner-flight-search-v1.p.rapidapi.com',
              useQueryString: true
            }
          };

          request(options, function (error, response, body) {
              if (error) throw new Error(error);
              console.log(body)
              this.setState({loading: true});
              this.setState({number: body["Quotes"].length});
              var values = [];
              var display = [];
              var carriers = {};
              for(var a = 0; a < body["Carriers"].length; a++){
                carriers[parseInt(body["Carriers"][a]["CarrierId"])] = String(body["Carriers"][a]["Name"])
              }
              console.log(carriers)
              for(var i = 0; i < body["Quotes"].length; i++){
                console.log()
                var airline = String(carriers[body["Quotes"][i]["OutboundLeg"]["CarrierIds"][0]]);
                console.log(carriers[body["Quotes"][i]["OutboundLeg"]["CarrierIds"]])
                var price = body["Quotes"][i]["MinPrice"];
                var departure = body["Quotes"][i]["OutboundLeg"]["DepartureDate"]
                var date = departure.replace("T", " @")
                values.push({"Airline": airline, "Price": parseInt(price)});
                display.push("Price: $" + String(price) + ", Airline: " + airline + ", Date and Time: " + date)
              }
              this.setState({data: values});
              this.setState({displayList: display})
              console.log(display)
          }.bind(this));
        }
        
    }

    componentDidMount(){
      this.count += 1;
      this.run();
    }

    componentDidUpdate(){
      if (this.count < this.props.counter){
        this.counter += 1;
        this.componentDidMount();
      }
    }
  
    onClickHandler(event){
      this.setState({dropdown: event.target.value})
    }

    handleSelect(event){
      console.log("hello");
      event.preventDefault();
      if (this.state.data.length > 1){
        if (this.state.dropdown === "Price (High to Low)"){
          for (var i = 0; i < this.state.data.length; i++){
            for (var j = 0; j < this.state.data.length - i - 1; j++){
              if (this.state.data[j]["Price"] < this.state.data[j + 1]["Price"]){
                var temp = this.state.data[j]
                var temp2 = this.state.displayList[j]
                this.state.data[j] = this.state.data[j + 1];
                this.state.data[j + 1] = temp; 
                this.state.displayList[j] = this.state.displayLis[j + 1];
                this.state.displayLis[j + 1] = temp2; 
              }
            }
          }
        } else if (this.state.dropdown === "Price (Low to High)"){
          for (var i = 0; i < this.state.data.length; i++){
            for (var j = 0; j < this.state.data.length - i - 1; j++){
              if (this.state.data[j]["Price"] > this.state.data[j + 1]["Price"]){
                var temp = this.state.data[j]
                var temp2 = this.state.displayList[j]
                this.state.data[j] = this.state.data[j + 1];
                this.state.data[j + 1] = temp; 
                this.state.displayList[j] = this.state.displayLis[j + 1];
                this.state.displayLis[j + 1] = temp2; 
              }
            }
          }
        } else if (this.state.dropdown === "Airline (A-Z)"){
          for (var i = 0; i < this.state.data.length; i++){
            for (var j = 0; j < this.state.data.length - i - 1; j++){
              if (this.state.data[j]["Carriers"] > this.state.data[j + 1]["Carriers"]){
                var temp = this.state.data[j]
                var temp2 = this.state.displayList[j]
                this.state.data[j] = this.state.data[j + 1];
                this.state.data[j + 1] = temp; 
                this.state.displayList[j] = this.state.displayLis[j + 1];
                this.state.displayLis[j + 1] = temp2; 
              }
            }
          }
        }
      }
    }


    render() {
      return (
        <div id="list">{!this.state.loading ? <header class="semiheader">Loading...</header> : 
        (this.state.number <= 0 ? <header class="semiheader"> No Flights available</header>:
          <div>
            <div id="sidebar"> 
              <header class="semiheader">Sort Flights By</header>
              <form onSubmit={this.handleSelect}>
                  <div className="form-group">
                    <select
                      value={this.state.dropdown}
                      onChange={this.onClickHandler}
                      className="form-control"
                      id="paymentMethod"
                    >
                      <option value="Default">Default</option>
                      <option value="Price (High to Low)">Price (High to Low)</option>
                      <option value="Price (Low to High)">Price (Low to High)</option>
                      <option value="Airline (A-Z)">Airline (A-Z)</option>
                    </select>
                  </div>
                  <button
                    type="submit"
                    id="reload"
                    className="btn btn-primary"
                    disabled={this.state.selectedValue === "Default"}
                  >
                    Reload
                  </button>
                </form>
              </div>
              <React.Fragment id="list">
                <header className="flights">Available Flights</header>
                <ul className="list-group">
                  {(this.state.displayList !== null ? (this.state.displayList.map(element => (
                    <li className="elements">
                      {element}
                    </li>
                  ))) : <p> Error </p>)}
                </ul>
              </React.Fragment>
            </div>
          )}
        </div>
      );
    }
  }

  export default List;
