import './App.css';
import Input from './Input';
import List from './List';
import React from 'react';

class App extends React.Component{
  constructor(props) {
    super(props);
    this.state = {submitted: false, results: null};
    this.changeSubmitState = this.changeSubmitState.bind(this)
    this.handleResults = this.handleResults.bind(this)
  }


  changeSubmitState() {
    this.setState({submitted: true})
  }

  handleResults(data){
    //alert(this.state.results)
    this.setState({results: data});
  }

  render(){
    const submit = this.state.submitted;
    const status =  () => {
      if (submit){
        return (<List value={this.state.results}/>);
      } 
    }
    return (
      <div>
        <header id="title">Finding the Best, Yet Most Affordable Flights</header>
        <Input submitState={this.changeSubmitState} returnResults={this.handleResults}/>
        {status}
      </div>
    );
  }



}


export default App;
