import './input.css';
import React from 'react';
import {FormControl, FormGroup, ControlLabel, HelpBlock, Checkbox, Radio, Button} from 'react-bootstrap';

class Input extends React.Component {
    constructor(props) {
      super(props);
      this.state = {country: '', currency: '', locale: '', origin: '', destination: '', outbound: '', inbound: ''};
  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      let name = event.target.name;
      let value = event.target.value;
      this.setState({[name]: value});
    }
  
    handleSubmit(event) {
      console.log(this.state)
      this.props.submitState();
      this.props.returnResults(this.state)
      event.preventDefault();

    }
  
    render() {
      return (
        <div>
            <header>Find a Flight:</header>
            <form id="theform" onSubmit={this.handleSubmit}>
            <FormGroup controlId="formControlsText">
              <ControlLabel>Text</ControlLabel>
              <FormControl type="text" placeholder="Enter text" />
            </FormGroup> 
            <label>
                Country:
                <input type="text" name='country' value={this.state.country} onChange={this.handleChange} />
            </label>
            <label>
                Currency:
                <input type="text" name='currency' value={this.state.currency} onChange={this.handleChange} />
            </label>
            <label>
                Locale:
                <input type="text" name='locale' value={this.state.locale} onChange={this.handleChange} />
            </label>
            <label>
                Origin:
                <input type="text" name='origin' value={this.state.origin} onChange={this.handleChange} />
            </label>
            <label>
                Destination:
                <input type="text" name='destination' value={this.state.destination} onChange={this.handleChange} />
            </label>
            <label>
                Outbound Date:
                <input type="text" name='outbound' value={this.state.outbound} onChange={this.handleChange} />
            </label>
            <label>
                Inbound Date:
                <input type="text" name='inbound' value={this.state.inbound} onChange={this.handleChange} />
            </label>
            <input type="submit" value="Options" />
            </form>
        </div>
      );
    }
  }


export default Input;
