import './input.css';
import React from 'react';
import { getJSON } from 'jquery';
import {FormControl, Dropdown, DropdownButton, FormGroup, FormLabel, HelpBlock, Checkbox, Radio, Button} from 'react-bootstrap';

class List extends React.Component {
    constructor(props) {
      super(props);
      this.state = {loading: false, data: null, number: 0, dropdown: "default"};
      this.componentDidMount = this.componentDidMount.bind(this);
      this.onClickHandler = this.onClickHandler.bind(this);
      this.handleSelect = this.handleSelect.bind(this);
    }

    componentDidMount(){
        var result = this.props.value;
        if(result != null){
            const request = require('request');
            require('JSON');
            const options = {
            method: 'GET',
            url: "https://skyscanner-skyscanner-flight-search-v1.p.rapidapi.com/apiservices/browseroutes/v1.0/" + result["country"] + "/" + result["currency"] + "/" + result["locale"] +
            "/" + result["origin"] + "/" + result["destination"] + "/" + result["outbound"] + "?inboundpartialdate=" + "/" +
            result["inbound"],
            json:true,
            headers: {
                'x-rapidapi-key': 'eee60225dfmsh483cdc041112e38p1614f2jsn1e3e3516465d',
                'x-rapidapi-host': 'skyscanner-skyscanner-flight-search-v1.p.rapidapi.com',
                useQueryString: true
            }
            };

            request(options, function (error, response, body) {
                if (error) throw new Error(error);
  
                this.setState({loading: true});
                this.setState({number: body["Routes"].length});
                var values = [];
                for(var i = 0; i < body["Routes"].length; i++){
                  values.push({"Airline": body["Carriers"][i]["Name"], "Price": body["Routes"][i]["Price"]});
                }
                console.log(values)
                this.setState({data: values});
            }.bind(this));
        }
        
    }
  
    onClickHandler(){
      console.log(1)
    }

    handleSelect(e){
      this.setState({dropdown: e})
      console.log(this.state.dropdown)
    }


    render() {
      return (
        <div>{!this.state.loading ? <p>Loading...</p> : 
        (this.state.number <= 0 ? <p> No Flights available</p>: 
          <DropdownButton id="dropdown-basic-button" title="Sort By" onSelect={handleSelect}>
            <Dropdown.Item href="#/action-1"></Dropdown.Item>
            <Dropdown.Item href="#/action-2">Price (Low to High)</Dropdown.Item>
            <Dropdown.Item href="#/action-3">Price (High to Low)</Dropdown.Item>
            <Dropdown.Item href="#/action-3">Airline (A-Z)</Dropdown.Item>
          </DropdownButton>

          )}</div>
      );
    }
  }

  export default List;
